mapper.select:
SELECT id, project_name, doc, gmt_create, gmt_modified, is_deleted FROM project
AND project_name LIKE CONCAT('%', #{project.projectName}, '%')
AND is_deleted = #{project.isDeleted}
AND (is_deleted = 0 OR is_deleted IS NULL)
ORDER BY gmt_create DESC LIMIT #{pageSize} OFFSET #{offset}

mapper.delete:
UPDATE project
SET is_deleted = 1
WHERE id = #{id}
